document.addEventListener("DOMContentLoaded", () => {

    const videoBackgrounds = new VideoBackgrounds('[data-vbg]');

    gsap.registerPlugin(ScrollTrigger);

    if (document.getElementById('video1')) {
        let video1 = gsap.timeline();
        video1.to('.home-video1__footer', {
            visibility: 'visible',
            duration: 0.1,
        });
        video1.from('.home-video1__logo .d-block', {
            opacity: 0,
            x: -130,
            duration: 0.6,
            ease: "power3.InOut",
        });
        video1.from('.home-video1__footer__center__line', {
            width: 0,
            duration: 0.3,
            ease: "power3.InOut",
        });
        video1.from('.home-video1__footer__right', {
            opacity: 0,
            x: 50,
            duration: 0.6,
            ease: "power3.InOut",
        });

        ScrollTrigger.create({
            animation: gsap.fromTo('#video1',
                {opacity: 1},
                {opacity: 0}),
            trigger: '#video1',
            start: 'top top',
            end: 'bottom',
            scrub: true,

        });

    }

    Array.from(document.querySelectorAll('.animate-right')).forEach((el) => {
        gsap.from(el, {
            scrollTrigger: {
                trigger: el,
                start: "20% 80%",
            },
            x: 50,
            opacity: 0,
            duration: 0.6,
            ease: "power3.InOut",
            stagger: .2,

        });
    });
});
